import React, {useState} from "react";
import Button from "./UI/Button";
import styles from "./Form.module.css";
import Card from "./UI/Card";
const Form = (props) => {
       const [nameInput, SetNameInput] = useState('')
       const [ageInput, SetAgeInput] = useState('')
       
    const usernameHandler = (event) => {
        SetNameInput(event.target.value)
    }
    const ageHandler = event => {
        SetAgeInput(event.target.value)
      
    }
    if(ageInput >=0 || ageInput.value && nameInput.value === ''){
        return alert('InvalidInfo')
    }
     
    const addUserHandler = (event) => {
    event.preventDefault();


   
     console.log(ageInput,nameInput)
     SetNameInput('')
     SetAgeInput('')
   }
  
    return (
   <Card className={styles.input}>
    <form onSubmit={addUserHandler} >
      <label htmlFor="username"> Username</label>
        <input id="username" value={nameInput} type="text" onChange={usernameHandler}/>
      <label htmlFor="age">Age(Years)</label>
      <input  id='age' value={ageInput} type="number" onChange={ageHandler} />
      <Button type='submit'>Add User</Button>
    </form>
    </Card>
  );
};

export default Form;
